namespace Olegnax\Athlete2\Block\System\Config\Form\Field;

class TestImageHeader extends \Magento\Config\Block\System\Config\Form\Field {
    public function __construct(
    \Magento\Backend\Block\Template\Context $context, array $data = []
    ) {
        parent::__construct($context, $data);
    }

    protected function _getElementHtml(\Magento\Framework\Data\Form\Element\AbstractElement $element) {
        $html = $element->getElementHtml();
        $value = $element->getData('value');

        $html .= '<script type="text/javascript">
require(["jquery", "Olegnax_Athlete2/select-image/select-image"], function ($) {
	$(function () {
		$("#' . $element->getHtmlId() . '").selectimage();
	});
});
</script>';
        return $html;
    }

}